<?php 	ini_set("allow_url_fopen", true);	set_time_limit(0);	$PHPData = $_REQUEST["PHPData"];	$old_files 	= $PHPData[0]; //az előző fájl neve	$files 		= $PHPData[1]; // itt már benne van az uploaddir is, mert az img src-ből kapja	$uploaddir = $_REQUEST["site_img"];	for ($i = 0; $i < count($old_files); $i++) {		if (substr($old_files[$i], 0, 4) != "http")			unlink("../" . $uploaddir . $old_files[$i]);	}	for ($i = 0; $i < count($files); $i++) {		$file_to_rename = "../" . $files[$i]; 		rename($file_to_rename, str_replace('_tmp_','',$file_to_rename));	}?>