<?php	require('connect.php');	$PHPData = $_REQUEST["PHPData"];	$newsid = 		$PHPData[0];	$siteid =		$PHPData[1];	require("site_params.php");  	echo "<interval>" . $Site_MailBulkInterval . "</interval>";	echo "<bulksize>" . $Site_MailBulkSize . "</bulksize>";	$query = "SELECT SQL_CALC_FOUND_ROWS DISTINCT Client.id as Cli_id, Client_Username, Client_ForName, Client_SurName, Client_Company, Client_Email 	FROM Client 	INNER JOIN Client_MailGroups 	ON Client_MailGroups.Client_id = Client.id	INNER JOIN News_MailGroups 		ON News_MailGroups.Client_MailGroup_id = Client_MailGroups.Client_MailGroup_id	INNER JOIN News 				ON News.id = News_MailGroups.News_id 	WHERE Client.Client_Status = 1 AND News.id = $newsid"; 	$result	= mysqli_query($con, $query);	$result2 = mysqli_query($con, "SELECT FOUND_ROWS()");		$row2 = mysqli_fetch_array($result2);		$rowcou = $row2["FOUND_ROWS()"]; 	echo "<bulktotal>$rowcou</bulktotal>";	$aray = array();	$aray2 = array();		while ($row = mysqli_fetch_assoc($result)) {		array_push($aray, $row['Client_Email']);		array_push($aray2, $row['Cli_id']);	} 	mysqli_close($con);  	 	echo "<mails>"; 	echo(json_encode($aray)); 	echo "</mails>"; 	echo "<ids>"; 	echo(json_encode($aray2)); 	echo "</ids>";