<?php		include_once('connect.php');	$PHPData = $_REQUEST["PHPData"];			$content = 	$PHPData[0];	$mod =		$PHPData[1];	$limstart = $PHPData[2];	$lim = 		$PHPData[3];	$view =		$PHPData[4];	$order =	$PHPData[5];	$fields = 	$PHPData[6];	$searches = $PHPData[7];	$customord =$PHPData[8];	$incdesc = 	$PHPData[9];/* 	$srcfield = $PHPData[10]; */	$imgpath = $_REQUEST["site_img"];			$query = "SELECT SQL_CALC_FOUND_ROWS * FROM " . $view;		if ($searches != "") {		if (strpos($query,'WHERE') !== false) {			$query = $query . " AND (" . $searches . ") ";		} else			$query = $query . " WHERE (" . $searches . ") ";	}			if ($customord == "") {		$query = $query . " ORDER BY " . $order;		} else {		$query = $query . " ORDER BY " . $customord . " " . $incdesc . ", " . $order;			}	//	echo($query);	$all_result = mysqli_query($con, $query . " LIMIT 0,1");	$count_result = mysqli_query($con, "SELECT FOUND_ROWS() cou");	$count_row = mysqli_fetch_array($count_result);		$rowcou = $count_row['cou'];	/* 	if ($limstart > -1) $query = $query . " LIMIT " . $limstart . ", " . $lim; *//*	if ($limstart == -2) {		$limstart = 0;		$lim = $rowcou;	}	*/	if ($lim > 0) {		$query = $query . " LIMIT " . $limstart . ", " . $lim;	}	if ($limstart == -1) $limstart = $rowcou - $steps;		$result = mysqli_query($con, $query);		$till = $limstart + ($lim == 0 ? $rowcou : $lim);	$till = ($till > $rowcou ? $rowcou : $till);	echo '	<div class="gridJump"><div id="gridTitle"></div><div class="gridJumpCont">	<img class="icon" src="images/first.png" onclick="_gridShow(\''.$content.'\',0,\''.$customord.'\');">	<img class="icon" src="images/prev.png"  onclick="_gridShow(\''.$content.'\',1,\''.$customord.'\');">';		echo '<div id="jumper">' . ($limstart + 1) . '-' . $till . ' (<div id="rowcou">' . $rowcou . '</div>)</div>';		echo '	<img class="icon" src="images/next.png" onclick="_gridShow(\''.$content.'\',2,\''.$customord.'\');">	<img class="icon" src="images/last.png" onclick="_gridShow(\''.$content.'\',3,\''.$customord.'\');">		<select id="perpage" onchange="_gridShow(\''.$content.'\',4,\''.$customord.'\');">		<option value="10"'.($lim == 10 ? 'selected' : '').'>10/oldal</option>		<option value="50"'.($lim == 50 ? 'selected' : '').'>50/oldal</option>		<option value="100"'.($lim == 100 ? 'selected' :'').'>100/oldal</option>		<option value="0"'.($lim == 0 ? 'selected' : '').'>Ã¶sszes</option>	</select></div></div>';	// titles	echo '<table id="grid" class="grid"><tr class="noExc">';	for ($i = 0; $i < count($fields); $i++) {		echo '<th>' . $fields[$i];		if ($fields[$i+2] != 'image') {			echo '<img class="img_search" src="images\sort';			if ($fields[$i+1] == $customord) {				if ($incdesc == 'ASC') {					echo '_asc';				} else {					echo '_desc';				}			}			echo '.png"   onclick="_gridShow    (\''.$content.'\',0,\''.$fields[$i+1].'\');">';	/* 		echo '<img class="img_search" src="images\search.png" onclick="filterAddField(\''.$content.'\',\''.$customord.'\',\''.$fields[$i+1].'\', \''.$fields[$i]. '\', \'' .$fields[$i+2].'\' );"></th>'; */			echo '<img class="img_search" src="images\search.png" onclick="filterAddField(\''.$fields[$i+1].'\', \''.$fields[$i]. '\', \'' .$fields[$i+2].'\' );"></th>';		}				$i++;		$i++;	}		echo '</tr>';		while($row = mysqli_fetch_array($result)) {			//fields		echo '<tr onclick="_formShow(\'' . $content . '\',' . $row[$fields[0+1]] . ');">';//$fields[0+1] =ID		for ($i = 0; $i < count($fields); $i++) {			echo '<td>'; 						switch ($fields[$i+2]) {					default : {						echo $row[$fields[$i+1]]; break;					}					case 'checkbox' : {//						echo ($row[$fields[$i+1]] == 1 ? "Igen" : "Nem"); 						echo '<input type="checkbox" ' . ($row[$fields[$i+1]] == 1 ? "checked" : "") . ' onclick="return false"/>';break;					}					case 'ddl' : {						$dd  = array('ddl', $fields[$i+1], $row[$fields[$i+1]] );						include('dropdowns.php'); break;					}					case 'date' : {						if ($row[$fields[$i+1]])						echo date("Y.m.d", strtotime($row[$fields[$i+1]])); break;					}					case 'datetime' : {						if ($row[$fields[$i+1]])						echo date("Y.m.d H:i", strtotime($row[$fields[$i+1]])); break;					}					case 'image' : {						if ($row[$fields[$i+1]]) {							if (substr($row[$fields[$i+1]], 0, 4) == "http") 								echo "<img class='gridimg' src='" .  $row[$fields[$i+1]] . "'>";							else								echo "<img class='gridimg' src='" . $imgpath . $row[$fields[$i+1]] . "'>";													}						break;					}			}						echo '</td>';			$i++;			$i++;		}			echo '</tr>';	}		echo '		</table>		</div>	';	mysqli_close($con);