<?php	require('connect.php');		$sql = $_REQUEST["sql"];		$pars = $_REQUEST["pars"];		$par1 = $_REQUEST["par1"];		$par2 = $_REQUEST["par2"];		$par3 = $_REQUEST["par3"];		$par4 = $_REQUEST["par4"];		$par5 = $_REQUEST["par5"];		$par6 = $_REQUEST["par6"];		$par7 = $_REQUEST["par7"];		$par8 = $_REQUEST["par8"];		$par9 = $_REQUEST["par9"];			$stmt = mysqli_prepare($con, $sql);		switch ($pars) {		case 1:			mysqli_stmt_bind_param($stmt, 's', $par1);			break;		case 2:			mysqli_stmt_bind_param($stmt, 'ss', $par1, $par2);			break;		case 3:			mysqli_stmt_bind_param($stmt, 'sss', $par1, $par2, $par3);			break;		case 4:			mysqli_stmt_bind_param($stmt, 'ssss', $par1, $par2, $par3, $par4);			break;		case 5:			mysqli_stmt_bind_param($stmt, 'sssss', $par1, $par2, $par3, $par4, $par5);			break;		case 6:			mysqli_stmt_bind_param($stmt, 'ssssss', $par1, $par2, $par3, $par4, $par5, $par6);			break;		case 7:			mysqli_stmt_bind_param($stmt, 'sssssss', $par1, $par2, $par3, $par4, $par5, $par6, $par7);			break;		case 8:			mysqli_stmt_bind_param($stmt, 'ssssssss', $par1, $par2, $par3, $par4, $par5, $par6, $par7, $par8);			break;		case 9:			mysqli_stmt_bind_param($stmt, 'sssssssss', $par1, $par2, $par3, $par4, $par5, $par6, $par7, $par8, $par9);			break;	}		mysqli_stmt_execute($stmt);		echo mysqli_affected_rows($con);	mysqli_close($con);?>